import './App.css';

import React, { useState } from 'react';

const wishes = [
    "Да бъдеш здрав и щастлив всеки ден на живота си!",
    "Нека всяко утро ти донесе усмивка и радост.",
    "Животът да ти е пълен със смех, обич и успехи.",
    "Да откриеш красотата във всеки един ден.",
    "Здраве, любов и спокойствие – основният триедин на щастлив живот.",
    "Нека всяка твоя стъпка бъде стъпка към постижения и удовлетворение.",
    "Да те заобиколи положителната енергия и благополучие.",
    "Бъди като птица – лека, свободна и пълна с радост.",
    "Светлина и успехи в твоя житейски път!",
    "Да откриеш радостта в малките неща и внимание в големите."
];

function MyComponent() {
    const [randomWish, setRandomWish] = useState('');
    const [rating, setRating] = useState('');
    const [reviewText, setReviewText] = useState('');

    const generateWish = () => {
        const randomIndex = Math.floor(Math.random() * wishes.length);
        setRandomWish(wishes[randomIndex]);
    };
  };
    const shareOnSocialMedia = (socialMedia) => {
        const url = `https://cherrybymary.com/en?fortune=${encodeURIComponent(randomWish)}`;
        let shareUrl = '';

        switch (socialMedia) {
            case 'facebook':
                shareUrl = `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(url)}`;
                break;
            case 'instagram':
                shareUrl = `https://www.instagram.com/sharer.php?u=${encodeURIComponent(url)}`;
                break;
            case 'twitter':
                shareUrl = `https://twitter.com/intent/tweet?url=${encodeURIComponent(url)}&text=Check%20out%20this%20fortune:%20${encodeURIComponent(randomWish)}`;
                break;
            default:
                break;
        }

        window.open(shareUrl, '_blank');
    };







function App() {
  return (
    <div className="App">

        <h1>🌟 късметче с кафето 🌟</h1>
        <p id="fortune-display">И твоят късмет е....</p>


        <button onClick={generateWish}>цъкни за да разбереш </button>


        <div id="social-sharing-buttons">
            <a href="#" onclick="shareOnFacebook()">

          </a>
            <a href="#" onclick="shareOnTwitter()">

            </a>
            <a href="#" onclick="shareOnInstagram()">

            </a>
        </div>

        <form id="google-review-form">
                    <label for="review-rating">Rating:</label>
                    <div class="star-rating" id="review-rating" onclick="handleStarRating(event)">
                        <span class="star" data-value="1">★</span>
                        <span class="star" data-value="2">★</span>
                        <span class="star" data-value="3">★</span>
                        <span class="star" data-value="4">★</span>
                        <span class="star" data-value="5">★</span>
                    </div>

                    <label for="review-text">Review:</label>
                    <textarea id="review-text" name="review-text" rows="4" required></textarea>

                    <button type="submit" onclick="submitGoogleReview()">Submit Review</button>
                </form>

    </div>
  );
}

export default App;
